import "./App.css";
import QRCode from "react-qr-code";

import Navbar from "./compoenet/Navbar";
import { useState } from "react";

function App() {
  const [value, setValue] = useState();
  const [Bgcolor, setBgColor] = useState("#FFFF");
  const [color, setColor] = useState("#FFFF");
  const [size, setSize] = useState(200);
  const [mode, setMode] = useState("light");
  const toglerMode = () => {
    if (mode == "light") {
      setMode("dark");
      document.body.style.backgroundColor = "green";
    } else {
      setMode("dark");
      document.body.style.backgroundColor = "red";
    }
  };
  return (
    <>
      <Navbar toglerMode={toglerMode} />

      <div className={`contanier${props.mode}`}>
        <input
          type="text"
          placeholder="value of QR code"
          onChange={(e) => setValue(e.target.value)}
        ></input>
        <br />
        <br />
        <input
          type="text"
          placeholder="background color"
          onChange={(e) => setBgColor(e.target.value)}
        ></input>
        <br />
        <br />
        <input
          type="text"
          placeholder="color"
          onChange={(e) => setColor(e.target.value)}
        ></input>
        <br />
        <br />
        <input
          type="text"
          placeholder="value of QR code"
          onChange={(e) => setValue(e.target.value)}
        ></input>
        <br />
        <br />
        <input
          type="number"
          placeholder="size of number"
          onChange={(e) =>
            setSize(parseInt(e.target.value === "" ? 0 : e.target.value, 10))
          }
        ></input>
        <br />
        <br />
        {value && (
          <QRCode
            title="QR code"
            value={value}
            bgColor={Bgcolor}
            Color={color}
            size={size === "" ? 0 : size}
          />
        )}
      </div>
    </>
  );
}

export default App;
